import { useState } from "react";
import PlayListMock from "../../__mock__/playList.json";

function State1() {
  /* 
    문제 1.

    state를 다루기 위한 기초 문제입니다.
    음악 목록 10가지의 mock data가 있습니다.

    아래에 추가버튼을 눌러 목록에 리스트를 추가하고 
    삭제 버턴을 눌렀을 때 데이터가 삭제될 수 있도록 해주세요
  */

  console.log(PlayListMock.playlist);
  /* 데이터 콘솔에 찍어두었으니 확인해볼 것 */

  const [list, setList] = useState(PlayListMock.playlist);

  const [inputs, setInput] = useState({
    title: "",
    signer: "",
  });

  const { title, signer } = inputs;

  const onInputChange = (e) => {
    const { name, value } = e.target;
    setInput({
      ...PlayListMock.playlist,
      [name]: value,
    });
  };

  const onShowMusic = () => {};

  return (
    <>
      <h1>문제1</h1>
      <ul>
        {list.map((v) => (
          <li>
            <h3>{v.title}</h3>
            <p>{v.signer}</p>
            <button>삭제</button>
          </li>
        ))}
      </ul>
      <div>
        <p>
          곡명 : <input name="title" value={title} onChange={onInputChange} />
        </p>
        <p>
          가수/작곡 :{" "}
          <input name="signer" value={signer} onChange={onInputChange} />
        </p>
        <p>
          <button onClick={onShowMusic}>추가</button>
        </p>
        <p>
          <button>삭제</button>
        </p>
      </div>
    </>
  );
}
export default State1;
