import styled from "styled-components";
import { useState } from "react";

function Comment({ Comments, onRemoveComment }) {
  const [isEdit, setIsEdit] = useState(false);
  const [editComment, setEditComment] = useState("");

  const onEditBtn = () => {
    setIsEdit((prev) => !prev);
  };

  const onInputEdit = (event) => {
    setEditComment(event.target.value);
  };

  return Comments.map((comment) => (
    <S.CommentItem>
      <p>
        작성자: <span>{comment.User.nickname}</span>
      </p>
      <p>
        댓글 내용: <span>{comment.content}</span>
      </p>
      {isEdit ? (
        <p>
          <input />
          <button>수정완료</button>
        </p>
      ) : (
        <button onClick={onEditBtn}>수정</button>
      )}
      <button onClick={() => onRemoveComment(comment.id, comment.myComment)}>
        삭제
      </button>
    </S.CommentItem>
  ));
}
export default Comment;

const CommentItem = styled.li`
  border: 1px solid #000;
  margin: 10px;
`;

const S = {
  CommentItem,
};
