import { useState } from "react";
import PlayListMock from "../../__mock__/playList.json";

function State1() {
  /* 
    문제 1.

    state를 다루기 위한 기초 문제입니다.
    음악 목록 10가지의 mock data가 있습니다.

    아래에 추가버튼을 눌러 목록에 리스트를 추가하고 
    삭제 버턴을 눌렀을 때 데이터가 삭제될 수 있도록 해주세요
  */

  console.log(PlayListMock.playlist);
  /* 데이터 콘솔에 찍어두었으니 확인해볼 것 */

  const [lists, setLists] = useState(PlayListMock.playlist);

  const [inputs, setInputs] = useState({
    title: "",
    contents: "",
  });

  const { title, contents } = inputs;

  const onInputChange = (event) => {
    const { name, value } = event.target;

    setInputs({
      ...inputs,
      [name]: value,
    });
  };

  return (
    <>
      <h1>문제1</h1>
      <ul>
        {lists.map((v) => (
          <li>
            <h3>{v.title}</h3>
            <p>{v.signer}</p>
            <button>삭제</button>
            <hr></hr>
          </li>
        ))}
      </ul>
      <div>
        <p>
          곡명 : <input name="title" onChange={onInputChange} />
        </p>
        <p>
          가수/작곡 : <input name="contents" onChange={onInputChange} />
        </p>
        <p>
          <button>추가</button>
        </p>
      </div>
    </>
  );
}
export default State1;
