import { useState, useRef } from "react";
import styled from "styled-components";
import Comment from "../../components/2.state/comment";

function State2() {
  /*  
    문제 2.

    Q1. 아래 작성된 state의 mock data를 활용하여
        댓글 목록을 화면에 랜더링 해보세요 :)
        Components는 src/components/state/comment.js를 활용하세요
        
    Q2. 댓글 작성 수정 삭제 기능을 구현해보세요 :)
            1. 댓글 작성 기능
            2. 댓글 수정 기능
            3. 댓글 삭제 기능 ( 본인이 작성한 댓글만 삭제할 수 있습니다, myComment 활용 )
    */

  // Comments 배열 인덱싱을 위해 id값 추가
  const [post, setPost] = useState({
    title: "안녕하세요 여러분 김성용 강사입니다 :)",
    content: "오늘도 모두 화이팅입니다!",
    User: {
      nickname: "김성용",
      age: 20,
      height: 190,
    },
    Comments: [
      {
        id: 1,
        User: {
          nickname: "김사과",
        },
        content: "오늘도 화이팅입니다!",
        myComment: false,
        isEdit: false,
      },
      {
        id: 2,
        User: {
          nickname: "반하나",
        },
        content: "오늘도 화이팅입니다!",
        myComment: false,
        isEdit: false,
      },
      {
        id: 3,
        User: {
          nickname: "오렌지",
        },
        content: "오늘도 화이팅입니다!",
        myComment: false,
        isEdit: false,
      },
      {
        id: 4,
        User: {
          nickname: "이멜론",
        },
        content: "오늘도 화이팅입니다!",
        myComment: false,
        isEdit: false,
      },
      {
        id: 5,
        User: {
          nickname: "박수박",
        },
        content: "오늘도 화이팅입니다!",
        myComment: false,
        isEdit: false,
      },
    ],
  });

  // id값을 설정하기 위해 useRef 사용
  const commentId = useRef(post.Comments.length);

  // useState를 2개 사용하여 input값 감지
  const [inputNickname, setInputNickname] = useState("");
  const [inputContent, setInputContent] = useState("");

  const onChangeNickname = (event) => {
    setInputNickname(event.target.value);
  };

  const onChangeContent = (event) => {
    setInputContent(event.target.value);
  };

  const onAddComment = () => {
    setPost((prev) => ({
      ...prev,
      Comments: [
        ...prev.Comments,
        {
          id: commentId.current,
          User: {
            nickname: inputNickname,
          },
          content: inputContent,
          myComment: true,
          isEdit: false,
        },
      ],
    }));

    commentId.current += 1;
  };

  // id, myComment를 받아오고 myComment가 true일 때만 삭제
  const onRemoveComment = (id, myComment) => {
    myComment &&
      setPost((prev) => ({
        ...prev,
        Comments: prev.Comments.filter((comment) => comment.id !== id),
      }));
  };

  const onStartEdit = (id, content, myComment) => {
    const newEdit = { ...post };
    const newEditComment = newEdit.Comments.find(
      (comment) => comment.id === id
    );
    newEditComment.content = content;
  };

  return (
    <S.Wrapper>
      <h1>문제2</h1>
      <S.PostBox>
        <S.PostTitle>제목: {post.title}</S.PostTitle>
        <S.PostContent>내용: {post.content}</S.PostContent>
      </S.PostBox>
      <S.PostInfo>
        <p>
          작성자: <span>{post.User.nickname}</span>
        </p>
        <p>
          작성자 나이: <span>{post.User.age}</span>
        </p>
        <p>
          작성자 키: <span>{post.User.height}</span>
        </p>
      </S.PostInfo>
      <div>
        <p>
          댓글 수: <span>{post.Comments.length}</span>
        </p>
        <input placeholder="작성자" onChange={onChangeNickname} />
        <input placeholder="댓글 내용" onChange={onChangeContent} />
        <button onClick={onAddComment}>댓글 작성</button>
      </div>
      <S.CommentList>
        <Comment Comments={post.Comments} onRemoveComment={onRemoveComment} />
      </S.CommentList>
    </S.Wrapper>
  );
}
export default State2;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const PostBox = styled.div`
  background-color: #999;
  width: 360px;
  padding: 10px;
`;

const PostTitle = styled.p`
  font-size: 20px;
  font-weight: bold;
`;

const PostContent = styled.p`
  color: #fff;
`;

const PostInfo = styled.div`
  width: 360px;
  border: 3px solid #f00;
  padding: 10px;
  margin: 10px;

  p {
    display: flex;
    justify-content: space-around;
  }

  span {
    font-weight: bold;
  }
`;

const CommentList = styled.ul`
  width: 960px;
`;

const S = {
  Wrapper,
  PostBox,
  PostTitle,
  PostContent,
  PostInfo,
  CommentList,
};
