import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import productList from "../__mock__/products.json";

function DetailPage() {
  // const Review = productList.products.map((product) => product.Review);
  // console.log(Review);

  const params = useParams();
  const [product, setProduct] = useState();
  useEffect(() => {
    // productNumber가 params로 받은 Number가 같은 product를 찾아서,
    const product = productList.products.find(
      (product) => product.productNumber === params.productNumber
    );
    // 스테이트로 관리를 해준다.
    // console.log(product);
    setProduct(product);
  }, []);
  console.log(product);

  const { Review } = product;
  console.log(Review);

  return (
    <div>
      <p>상품의 번호: {params.productNumber}</p>
      <p>상품의 리뷰: </p>
      {/* 
      상세 페이지는 자유롭게 꾸미시면 됩니다.
      아직 해당 부분의 진도가 나가지 않았기 때문에 주소의 파람을 가지고 올 수 있는 방법은
      미리 콘솔에 찍어두었습니다.

      단, 없는 번호 상품으로 접근 시 state페이지로 돌아가도록 구현해주세요
    */}
    </div>
  );
}
export default DetailPage;
